                    x = (float)(Math.sin((float)(omega*n)));
                    ++n;
                    shortAudioData[i] = (short)(x * Short.MAX_VALUE);
                }

                audioTrack.write(shortAudioData, 0, shortAudioData.length);
            }

            audioTrack.stop();


        }
